import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [organizations, setOrganizations] = useState([]);

  useEffect(() => {
    axios.get('https://hcb.hackclub.com/api/v3/organizations', {
      headers: {
        'Accept': 'application/json'
      }
    })
    .then(response => {
      setOrganizations(response.data); // Assuming the API returns an array of organizations
    })
    .catch(error => {
      console.error('Error fetching organizations:', error);
    });
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Filter organizations based on searchTerm
  const filteredOrganizations = organizations
    .filter(org => org.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .slice(0, 9); // Only take the first 6 organizations

  return (
    <div className="app-container">
      <header>
        <h1>
          Hack Club Bank
        </h1>
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </header>
      <div className="grid-container">
        {filteredOrganizations.map((org, index) => (
          <div key={index} className="grid-item">
            <div className="org-name">{org.name}</div> {/* Replace `org.name` with actual property name from your API */}
            <div className="org-transactions">Transactions: {org.transactions}</div> {/* Replace `org.transactions` with actual property name from your API */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
