import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Popup from './Popup'; // Make sure this component is created as described earlier
import './App.css'; // Assuming your CSS styles are defined here

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [organizations, setOrganizations] = useState([]);
  const [activeTransactions, setActiveTransactions] = useState(null); // New state for managing active organization's transactions

  useEffect(() => {
    axios.get('https://hcb.hackclub.com/api/v3/organizations', {
      headers: {
        'Accept': 'application/json'
      }
    })
    .then(response => {
      setOrganizations(response.data); // Assuming the API returns an array of organizations
    })
    .catch(error => {
      console.error('Error fetching organizations:', error);
    });
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleBoxClick = (organizationId) => {
    axios.get(`https://hcb.hackclub.com/api/v3/organizations/${organizationId}/transactions`, {
      headers: { 'Accept': 'application/json' }
    })
    .then(response => {
      setActiveTransactions(response.data); // Store transactions for displaying in the popup
    })
    .catch(error => {
      console.error('Error fetching transactions:', error);
    });
  };

  const handleClosePopup = () => {
    setActiveTransactions(null); // Close the popup
  };

  // Filter organizations based on searchTerm
  const filteredOrganizations = organizations
    .filter(org => org.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .slice(0, 50); // Adjust the number to control how many organizations are displayed

  return (
    <div className="app-container">
      <header>
        <h1>Hack Club Bank</h1>
        <input
          type="text"
          placeholder="Companies..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </header>
      <div className="grid-container">
        {filteredOrganizations.map((org, index) => (
          <button key={index} className="grid-item" onClick={() => handleBoxClick(org.id)}>
            <div className="org-name">{org.name}</div>
            {/* Assuming each org object has an 'id' field */}
          </button>
        ))}
      </div>
      {activeTransactions && <Popup transactions={activeTransactions} onClose={handleClosePopup} />}
    </div>
  );
}

export default App;
