import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [organizations, setOrganizations] = useState([]);

  useEffect(() => {
    axios.get('https://hcb.hackclub.com/api/v3/organizations?limit=500', {
      headers: {
        'Accept': 'application/json'
      }
    })
    .then(response => {
      setOrganizations(response.data); 
    })
    .catch(error => {
      console.error('Error fetching organizations:', error);
    });
  }, []);

  const handleBoxClick = (orgId) => {
    // Perform the action when a box is clicked
    alert(`Organization with ID ${orgId} clicked`);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Filter organizations based on searchTerm
  const filteredOrganizations = organizations
    .filter(org => org.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .slice(0, 1000); // Only take the first 6 organizations

  return (
    <div className="app-container">
      <header>
        <h1>
          Hack Club Bank
        </h1>
        <input
          type="text"
          placeholder="Companies..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </header>
      <div className="grid-container">
        {filteredOrganizations.map((org, index) => (
          <button key={index} className="grid-item" onClick={() => handleBoxClick(org.id)}>
            <div className="org-name">{org.name}</div>
            <div className="org-transactions"> {org.transactions}</div>
          </button>
        ))}
      </div>
    </div>
  );
}

export default App;
